import UIKit

// Create an instance of LightSensor to gather light, and an instance of ToneOutput to play tones.
let lightSensor = LightSensor(configuration: .front)
let toneOutput = ToneOutput()

let timer = Timer.scheduledTimer(withTimeInterval: 0.1, repeats: true, block: { _ in
    // Create a tone based upon the brightness and hue (color) of the incoming light.
    let color = lightSensor.currentSample
    let toneValue = (color.brightness * 1000) * (color.hue + 1)
    let tone = Tone(pitch: toneValue, volume: 60)
    
    // Play the tone.
    toneOutput.play(tone: tone)
})
