public typealias _ImageLiteralType = Image

playgroundPrologue()
//#-end-hidden-code
//#-editable-code
// Create and set up the scene.
let scene = Scene()
scene.backgroundImage = #imageLiteral(resourceName: "background4.png")
scene.hasCollisionBorder = true
let floor = Sprite(image: #imageLiteral(resourceName: "floor1.png"), columns: 20, rows: 1, isDynamic: false, name: "floor")
scene.place(floor, at: Point(x: 0, y: -400))

// Create a Player and place on the scene.
var codeMachine = Player(type: .codeMachine, name: "codeMachine")
codeMachine.isAffectedByGravity = true
codeMachine.runAnimation(.walk, numberOfTimes: -1)
scene.place(codeMachine, at: Point(x: 0, y: 0))

// Create a Label to print text on the scene.
var debugLabel = Label(text: "Code Machine", color: #colorLiteral(red: 0.474509805440903, green: 0.839215695858002, blue: 0.976470589637756, alpha: 1.0), font: Font.Cochin, size: 50, name: "debug")
scene.place(debugLabel, at: Point(x: 0, y: 400))

// Add an event handler for when a finger moved event is received.
scene.setOnFingerMovedHandler( { touch in
    if touch.firstTouch {
        let xDirection = touch.position.x - codeMachine.position.x
        let yDirection = touch.position.y - codeMachine.position.y
        
        codeMachine.jump(x: xDirection, y: yDirection)
        playSound(.retroJump1)
    }
})

// Use brightness information from the LightSensor to change the scale of codeMachine.
let lightSensor = LightSensor()

lightSensor.setOnUpdateHandler( { color in
    codeMachine.scale = color.brightness * 5
    debugLabel.text = "Code Machine Scale: " + (color.brightness * 5).string(fractionDigits: 2)
})
//#-end-editable-code
//#-hidden-code
playgroundEpilogue()
//#-end-hidden-code


